// @ts-nocheck
import React from 'react'
import styled from 'styled-components'
import { Button, Heading, Text } from '@pancakeswap-libs/uikit'
import { useWallet } from '@binance-chain/bsc-use-wallet'
import useI18n from 'hooks/useI18n'
import Page from 'components/layout/Page'
import Container from 'components/layout/Container'
import HowItWorks from './components/HowItWorks'
import NftPreview from './components/NftPreview'
import { useNaughty } from 'hooks/useContract'



const StyledHero = styled.div`
  border-bottom: 1px solid ${({ theme }) => theme.colors.textSubtle};
  padding-bottom: 32px;
  padding-top: 32px;
  margin-bottom: 24px;
`

const Cta = styled.div`
  align-items: center;
  display: flex;

  & > a + a {
    margin-left: 16px;
  }
`
const { account } = useWallet()
const naughtyaddress = getNGHTYAddress()
const rabbitMintingContract = useRabbitMintingFarm(RABBIT_MINTING_FARM_ADDRESS)
const handleConfirm = async () => {
  try {
    

    await rabbitMintingContract.methods
      .burnNFT(tokenId)
      .send({ from: account })
      .on('sending', () => {
        setIsLoading(true)
      })
      .on('receipt', () => {
        onDismiss()
        onSuccess()
      })
      .on('error', () => {
        console.error(error)
        setError('Unable to burn NFT')
        setIsLoading(false)
      })
  } catch (err) {
    console.error('Unable to burn NFT:', err)
  }
}
const RegisterToWinPage = () => {
  const TranslateString = useI18n()

  return (
    <Page>
      <Container>
        <StyledHero>
          <Heading as="h1" size="xxl" color="secondary" mb="24px">
            NFTs
          </Heading>
          <Heading as="h2" size="lg" color="secondary" mb="16px">
            You must hold at least 69 000 Flix to enter the Naughty Area
          </Heading>
          <Text mb="24px"> If you can not wait to use your Naughty to farm Flix for free you can gain access here: </Text>
          <Cta>
            <Button
              as="a"
              href="https://docs.google.com/forms/d/e/1FAIpQLSfToBNlovtMvTZFSwOhk0TBiDPMGasLxqG0RB-kJN85HR_avA/viewform"
              target="_blank"
              rel="noopener noreferrer"
            >
              Gain access with Naughty
            </Button>
            <Button as="a" href="#how-it-works" variant="secondary">
              {TranslateString(620, 'Learn more')}
            </Button>
          </Cta>
        </StyledHero>
      </Container>
      <NftPreview />
      <HowItWorks />
    </Page>
  )
}

export default RegisterToWinPage
